import { Card, CardContent, CardMedia, Grid, Paper, Typography } from "@mui/material";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import fetchModel from "../../libs/fetchModelData";

function UserPhotos(props) {
    const {userId} = useParams();
    const [photos, setPhotos] = useState();
    const [user, setUser] = useState();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState();

    useEffect(() => {
        const fetchPhotos = async () => {
            try {
                const userResponse = await fetchModel(`api/user/${userId}`, "GET");
                if (userResponse.status===200) {
                    const userData = await userResponse.json();
                    console.log(`user: ${userData}`);
                    setUser(userData);
                }

                const photosResponse = await fetchModel(`api/photo/photosOfUser/${userId}`, "GET");
                const photosData = await photosResponse.json();
                console.log(`photos: ${photosData}`);
                setPhotos(photosData);
                // if (photosResponse.status===200) {
                //     const photosData = await photosResponse.json();
                //     console.log(`photos: ${photosData}`);
                //     setPhotos(photosData);
                // }
                console.log(photos);
            } catch(error) {
                console.log(error);
                setError(error);
            } finally {
                setLoading(false);
            }
        };
        fetchPhotos();
    }, [userId])
    if (props.loginUser) {
        if (loading) return <Typography>Loading...</Typography>;
        if (error) return <Typography color="error">Error: {error}</Typography>;
        if (!user) {
            return <Typography variant="body1">User not found</Typography>;
        }

        const formatDate = (dateString) => {
        const date = new Date(dateString);
            return date.toLocaleString();
        };
        return (
            <div>
                <Typography variant="h4" gutterBottom>
                    Photos of {user.first_name} {user.last_name}
                </Typography>
                <Grid container spacing={3}>
                    {photos.map((photo) => (
                        <Grid item xs={12} key={photo._id}>
                            <Card>
                                <CardMedia
                                    style={{ objectFit: "contain", width: '60%', height:'auto' }}
                                    component={"img"}
                                    //height="400"
                                    //image={`/images/${photo.file_name}`}
                                    image={photo.file_name}
                                    alt={`Photo by ${user.first_name}`}
                                >
                                </CardMedia>

                                <CardContent>
                                    <Typography variant="body2" color={"text.secondary"} gutterBottom>
                                        {formatDate(photo.date_time)}
                                    </Typography>

                                    <Typography variant="h6" gutterBottom>Comments</Typography>
                                    {photo.comments.map((cmt) => (
                                        <Paper key={cmt._id} sx={{ p: 2, mb: 2 }}>
                                            <Typography variant="body2" color={"text.secondary"}>
                                                {formatDate(cmt.date_time)}
                                            </Typography>
                                            <Typography variant="body1" component={`div`}>
                                                <Link to={`/users/${cmt.user_id}`}>
                                                    {cmt.user.first_name} {cmt.user.last_name}
                                                </Link>
                                                : {cmt.comment}
                                            </Typography>
                                        </Paper>
                                        
                                    ))}
                                </CardContent>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            </div>
        )
    }
}

export default UserPhotos;
