import { AppBar, Toolbar, Typography, Box, IconButton, Button, Grid } from "@mui/material";
import { useState } from "react";
import fetchModel from "../../libs/fetchModelData";
import { Navigate, useNavigate } from "react-router-dom";

function TopBar(props) {
    const [user, setUser] = useState();
    const [selectedImage, setSelectedImage] = useState(null);
    const [open, setOpen] = useState(false);
    const [previewUrl, setPreviewUrl] = useState(null);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    const handleLogout = async() => {
        const response = await fetchModel(`api/user/logout`, "POST");
        if (response.status === 200) {
            props.onLogin(null);
            navigate(`/login-register`);
        }
    }

    const handleCloseDialog = () => {
        setOpen(false);
        setSelectedImage(null);
        setPreviewUrl(null);
        setError(null);
    };

    const handleUploadImage = event => {
        const file = event.target.files[0];
        if (file) {
            setSelectedImage(file);
            setPreviewUrl(URL.createObjectURL(file));
            setOpen(true);
            setError(null);
        }
    }

    const handleUploadSubmit = async () => {

    }

    useEffect(() => {
        return () => {
            if (previewUrl) URL.revokeObjectURL(previewUrl);
        };
    }, [previewUrl]);

    if (props.loginUser) {
        return (
            <AppBar className="topbar-appBar" position="absolute">
                <Toolbar>
                <Typography variant="h5" color="inherit" sx={{ flexGrow: 1 }}>
                   Hi {props.loginUser.first_name} {props.loginUser.last_name}
                </Typography>
                <Grid item xs={6} sm={4} md={2} lg={2}>
                    <input
                        type="file"
                        accept="image/*"
                        onChange={handleUploadImage}
                        style={{ display: "none" }}
                        id="upload-image-input"
                        name="photo"
                    />
                    <label htmlFor="upload-image-input">
                        <Button
                        variant="contained"
                        sx={{
                            textTransform: "none",
                            fontWeight: 600,
                            px: 2,
                            py: 1,
                            borderRadius: 2,
                            bgcolor: "#4caf50", // Xanh lá
                            color: "white",
                            mr: 2,
                            "&:hover": {
                            bgcolor: "#388e3c", // Xanh lá đậm
                            },
                        }}
                        component="span"
                        >
                        Upload
                        </Button>
                    </label>
                </Grid>

                <Button 
                    title="Log out" 
                    onClick={handleLogout} 
                    variant="contained"
                    sx={{
                        textTransform: "none",
                        fontWeight: 500,
                        px: 2,
                        py: 1,
                        borderRadius: 2,
                        borderColor: "white",
                        color: "white",
                        "&:hover": {
                        bgcolor: "rgba(255, 255, 255, 0.1)",
                        borderColor: "white",
                        },
                    }}
                >
                    Logout
                </Button>
                
                </Toolbar>
            </AppBar>
        );
    } else {
        return (
            <AppBar className="topbar-appBar" position="absolute">
                <Toolbar>
                <Typography variant="h5" color="inherit" sx={{ flexGrow: 1 }}>
                    Please Login
                </Typography>
                <Box sx={{ flexGrow: 0 }}>
                    <Typography variant="h6" color="inherit">
                    Photo sharing app
                    </Typography>
                </Box>
                </Toolbar>
            </AppBar>
            );
    }
}

export default TopBar;
