import { AppBar, Toolbar, Typography, Box, IconButton, Button } from "@mui/material";
import { useState } from "react";
import fetchModel from "../../libs/fetchModelData";
import { Navigate, useNavigate } from "react-router-dom";
import {LogoutIcon} from "@mui/icons-material/Logout";

function TopBar(props) {
    const [user, setUser] = useState();
    const navigate = useNavigate();

    const handleLogout = async() => {
        const response = await fetchModel(`api/user/logout`, "POST");
        if (response.status === 200) {
            props.onLogin(null);
            navigate(`/login-register`);
        }
    }
    if (props.loginUser) {
        return (
            <AppBar className="topbar-appBar" position="absolute">
                <Toolbar>
                <Typography variant="h5" color="inherit" sx={{ flexGrow: 1 }}>
                   Hi {props.loginUser.first_name} {props.loginUser.last_name}
                </Typography>
                <Button 
                    // title="Log out" 
                    // onClick={handleLogout} 
                    // variant="contained"
                    variant="outlined"
                    color="inherit"
                    startIcon={<LogoutIcon />}
                    sx={{
                        textTransform: "none",
                        fontWeight: 500,
                        px: 2,
                        py: 1,
                        borderRadius: 2,
                        borderColor: "white",
                        color: "white",
                        "&:hover": {
                        bgcolor: "rgba(255, 255, 255, 0.1)",
                        borderColor: "white",
                        },
                    }}
                >
                    {/* <ExitToApp style={{ color: "#ffffff", marginBottom:"8px" }} fontSize="small" /> */}
                    Logout
                </Button>
                {/* <Box sx={{ flexGrow: 0 }}>
                    <Typography variant="h6" color="inherit">
                    Photo sharing app
                    </Typography>
                </Box> */}
                </Toolbar>
            </AppBar>
        );
    } else {
        return (
            <AppBar className="topbar-appBar" position="absolute">
                <Toolbar>
                <Typography variant="h5" color="inherit" sx={{ flexGrow: 1 }}>
                    Please Login
                </Typography>
                <Box sx={{ flexGrow: 0 }}>
                    <Typography variant="h6" color="inherit">
                    Photo sharing app
                    </Typography>
                </Box>
                </Toolbar>
            </AppBar>
            );
    }
}

export default TopBar;
