import { useEffect, useState } from "react";
import fetchModel from "../../libs/fetchModelData";
import { useParams } from "react-router-dom";
import { Typography } from "@mui/material";

function UserDetail(props) {
    const { userId } = useParams();
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUser = async() => {
            try {
                const user = await fetchModel(`api/user/${userId}`, "GET");
                setUser(user);
            } catch(error) {
                console.log(error);
                setLoading(false);
                setError(error);
            } finally {
                setLoading(false);
            }
        };
        fetchUser();
    }, [])

    if(loading) return <Typography>Loading...</Typography>
    if(error) return <Typography color={"red"}>Error: {error}</Typography>
    if (!user) {
      return <Typography variant="body1">User not found</Typography>;
    }

    if(props.loginUser) {
        return (
            <div>
                <Typography variant="h4" gutterBottom>
                    {user.first_name} {user.last_name}
                </Typography>
                
            </div>
        )
    } else {
        return <Typography>Please login to see this page</Typography>
    }
}

export default UserDetail;
